package ticket.booking;

import ticket.booking.entities.User;
import ticket.booking.services.UserBookingService;

import java.io.IOException;
import java.util.Scanner;

public class App {

    public static void main(String[] args) {
        try {
            System.out.println("********** üöÑ Ticket Booking System üõ§Ô∏è **********");

            Scanner sc = new Scanner(System.in);
            int option = 0;
            UserBookingService userBookingService;

            try {
                userBookingService = new UserBookingService();
            } catch (IOException ex) {
                System.out.println("Error occurred while initializing the service üòü");
                return;
            }

            while (option != 7) {
                System.out.println("Choose option");
                System.out.println("1. Sign up");
                System.out.println("2. Login");
                System.out.println("3. Fetch Bookings");
                System.out.println("4. Search Trains");
                System.out.println("5. Book a Seat");
                System.out.println("6. Cancel my Booking");
                System.out.println("7. Exit the App");

                option = sc.nextInt();
                String name, password;

                switch (option) {
                    case 1:
                        sc.nextLine();
                        System.out.println("Enter your Name üôè");
                        name = sc.nextLine();

                        System.out.println("Enter your password üßë‚Äçüíª");
                        password = sc.next();

                        User newUser = new User(name, password);

                        userBookingService.signUp(newUser);
                        System.out.println("User signed up successfully üöÄ\n");
                        break;

                    case 2:
                        sc.nextLine();
                        System.out.println("Enter your Name üê£");
                        name = sc.nextLine();

                        System.out.println("Enter your password üóùÔ∏è");
                        password = sc.next();

                        if (userBookingService.loginUser(name, password)) {
                            System.out.println("User " + name + " logged in successfully ‚úÖ");
                        } else {
                            System.out.println("Uh-hu! Invalid credentials. Please try again ‚ùå");
                        }
                        System.out.println();
                        break;

                    case 3:
                        System.out.println("Fetching your bookings...");
                        userBookingService.fetchBooking();
                        System.out.println();
                        break;

                    case 4:

                        break;

                    default:
                        System.out.println("Invalid option. Please try again!!!");
                }
            }
        } catch (Exception e) {
            e.printStackTrace();
            System.out.println("Error: " + e.getLocalizedMessage());
        }
    }
}
